import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {SharedService} from "../shared.service";

@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.css']
})
export class UpdateComponent implements  OnInit{
  hero:any;
  id:any;
  constructor( private act: ActivatedRoute,private _shared:SharedService,private router:Router) {
  }
  update(){
    this._shared.updatehero(this.id,this.hero).subscribe(
      res=>{
        console.log(res);
        this.router.navigate(['/list']);
      },error => {
        console.log(error)
      }
    )
  }
  ngOnInit():void {
    this.id=this.act.snapshot.paramMap.get('id');
    this._shared.getherobyid(this.id).subscribe(
      res=>{
        this.hero=res;
      },err=>{
        console.log(err);
      }
    )
    console.log(this.id);
  }

}
<div class="row mt-5">
  <div class="col-3">
    <input [(ngModel)]="hero.name" type="text" class="form-control m-1" placeholder="name">
  </div>
  <div class="col-3">
    <input [(ngModel)]="hero.power" type="text" class="form-control m-1" placeholder="power">
  </div>
  <div class="col-3">
    <input type="text" [(ngModel)]="hero.imageurl"class="form-control m-1" placeholder="imageurl">
  </div>
  <div class="col-3">
    <button (click)="update()" type="button" class="btn form-control m-1 text-light bg-black"> update</button>
  </div>
</div>
